# Create a binary file with name and roll no. Then Seach for a given roll no. and display the name, if not found display appropriate message
import pickle; f1 = open("1.pkl", "wb"); n = int(input("Enter the number of students: ")); [pickle.dump((input("Enter the name: "), input("Enter the roll number: ")), f1) for _ in range(n)]; f1.close(); f2 = open("1.pkl", "rb"); roll = input("Enter the roll number to search: "); found = False; [print(f"Name: {name}") or (found := True) for name, r in iter(lambda: pickle.load(f2), None) if r == roll]; f2.close(); print("Roll number not found") if not found else None
